import React, { createContext, useContext, useMemo, useState } from 'react';
import { StyleSheet, TouchableOpacity, Text, View } from 'react-native';

// Elegant, muted palette
export const colors = {
  primary: '#2b6cb0', // deep blue
  accent: '#c084fc', // soft purple
  background: '#f7f7fb',
  surface: '#ffffff',
  muted: '#eef2ff',
  text: '#1f2937',
  textMuted: '#6b7280',
  success: '#16a34a',
  danger: '#ef4444'
};

const buildGlobal = (t) => StyleSheet.create({
  container: { flex: 1, padding: t.spacing.container || 16, backgroundColor: t.colors.background },
  card: { backgroundColor: t.colors.surface, borderRadius: 10, padding: t.spacing.card || 12, marginBottom: 10, shadowColor: '#000', shadowOpacity: 0.06, shadowRadius: 6, elevation: 2 },
  header: { fontSize: 18, fontWeight: '700', color: t.colors.text },
  subText: { fontSize: 13, color: t.colors.textMuted },
  smallText: { fontSize: 12, color: t.colors.textMuted },
  input: { borderWidth: 1, borderColor: t.colors.inputBorder || '#e6e9ee', backgroundColor: t.colors.inputBackground || '#fff', padding: 10, borderRadius: 8, marginBottom: 8 },
  row: { flexDirection: 'row', alignItems: 'center' },
  spaceSmall: { height: 8 },
  pillContainer: { flexDirection: 'row', backgroundColor: t.colors.muted, borderRadius: 22, padding: 6 },
  pill: { paddingVertical: 6, paddingHorizontal: 14, borderRadius: 18, marginRight: 6 },
  pillText: { fontWeight: '600' },
  button: { backgroundColor: t.colors.primary, paddingVertical: 10, paddingHorizontal: 14, borderRadius: 8, alignItems: 'center' },
  buttonText: { color: '#fff', fontWeight: '600' }
});

// Theme context and provider
const defaultTheme = {
  colors,
  spacing: { container: 16, card: 12 }
};

const ThemeContext = createContext({ theme: defaultTheme, setTheme: () => {} });

export function ThemeProvider({ children, initialTheme }) {
  const [theme, setTheme] = useState(initialTheme || defaultTheme);
  const value = useMemo(() => ({ theme, setTheme }), [theme]);
  return (
    <ThemeContext.Provider value={value}>{children}</ThemeContext.Provider>
  );
}

export function useTheme() {
  const ctx = useContext(ThemeContext);
  return ctx ? ctx.theme : defaultTheme;
}

export function useThemedStyles() {
  const theme = useTheme();
  const s = useMemo(() => buildGlobal(theme), [theme]);
  return { s, theme };
}

// default exports for backward compatibility
export default { colors, buildGlobal, ThemeProvider, useTheme, useThemedStyles };
