import React from 'react';
import { StyleSheet, TouchableOpacity, Text, View } from 'react-native';

// Elegant, muted palette
export const colors = {
  primary: '#2b6cb0', // deep blue
  accent: '#c084fc', // soft purple
  background: '#f7f7fb',
  surface: '#ffffff',
  muted: '#eef2ff',
  text: '#1f2937',
  textMuted: '#6b7280',
  success: '#16a34a',
  danger: '#ef4444'
};

export const global = StyleSheet.create({
  container: { flex: 1, padding: 16, backgroundColor: colors.background },
  card: { backgroundColor: colors.surface, borderRadius: 10, padding: 12, marginBottom: 10, shadowColor: '#000', shadowOpacity: 0.06, shadowRadius: 6, elevation: 2 },
  header: { fontSize: 18, fontWeight: '700', color: colors.text },
  subText: { fontSize: 13, color: colors.textMuted },
  smallText: { fontSize: 12, color: colors.textMuted },
  input: { borderWidth: 1, borderColor: '#e6e9ee', backgroundColor: '#fff', padding: 10, borderRadius: 8, marginBottom: 8 },
  row: { flexDirection: 'row', alignItems: 'center' },
  spaceSmall: { height: 8 },
  pillContainer: { flexDirection: 'row', backgroundColor: colors.muted, borderRadius: 22, padding: 6 },
  pill: { paddingVertical: 6, paddingHorizontal: 14, borderRadius: 18, marginRight: 6 },
  pillText: { fontWeight: '600' },
  button: { backgroundColor: colors.primary, paddingVertical: 10, paddingHorizontal: 14, borderRadius: 8, alignItems: 'center' },
  buttonText: { color: '#fff', fontWeight: '600' }
});

export function PillToggle({ options = [], value, onChange }) {
  return (
    <View style={global.pillContainer}>
      {options.map(opt => (
        <TouchableOpacity
          key={opt.value}
          onPress={() => onChange(opt.value)}
          style={[global.pill, { backgroundColor: value === opt.value ? colors.primary : 'transparent' }]}
        >
          <Text style={[global.pillText, { color: value === opt.value ? '#fff' : colors.primary }]}>{opt.label}</Text>
        </TouchableOpacity>
      ))}
    </View>
  );
}

export default { colors, global, PillToggle };
