import React, { useState, useEffect, useCallback } from 'react';
import { View, TextInput, Button, Text, Alert, TouchableOpacity, Platform } from 'react-native';
import Loading from '../components/Loading';
import styles, { PillToggle, global, colors } from '../styles';
import { useFocusEffect } from '@react-navigation/native';
import * as DocumentPicker from 'expo-document-picker';
import AsyncStorage from '@react-native-async-storage/async-storage';

const API_BASE = Platform.OS === 'web'
  ? `http://${window.location.hostname}:8000/api`
  : 'http://10.0.2.2:8000/api'; // emulator host for Android

export default function HomeScreen({ navigation }) {
  const [isAdmin, setIsAdmin] = React.useState(false);
  const [tickets, setTickets] = React.useState([]);
  const [openCount, setOpenCount] = React.useState(0);

  React.useEffect(() => {
    (async () => {
      const v = await AsyncStorage.getItem('isAdmin');
      const admin = v === '1';
      setIsAdmin(admin);
      if (admin) {
  // load admin tickets for messenger view (respecting adminFilter)
  await loadAdminTickets(adminFilter);
      }
      else {
        // load user's own ongoing reports (open tickets)
        const token = await AsyncStorage.getItem('authToken');
        try {
          // fetch paginated open tickets to get count quickly
          const res = await fetch(`${API_BASE}/tickets/my/?is_open=true`, { headers: token ? { Authorization: `Token ${token}` } : {} });
          if (res.ok) {
            const data = await res.json();
            // if paginated, data.count is available
            const count = data.count != null ? data.count : (Array.isArray(data) ? data.length : 0);
            setOpenCount(count);
            const list = data.results || data;
            const open = list.map(t => ({ uid: t.uid, title: t.title, name: `${t.first_name} ${t.last_name}`, unread: t.unread_count || 0, last_at: t.updated_at }));
            setTickets(open);
          }
        } catch (e) { console.log('Failed load my tickets', e); }
      }
    })();
  }, []);

  const [adminFilter, setAdminFilter] = useState('not_complete'); // 'not_complete' | 'all' | 'completed'
  const [loadingAdmin, setLoadingAdmin] = useState(false);

  const loadAdminTickets = async (which = 'not_complete') => {
    setLoadingAdmin(true);
    const token = await AsyncStorage.getItem('authToken');
    const headers = token ? { Authorization: `Token ${token}` } : {};
    try {
      if (which === 'not_complete') {
        const res = await fetch(`${API_BASE}/admin/tickets/pending/`, { headers });
        if (!res.ok) { setTickets([]); setLoadingAdmin(false); return; }
        const data = await res.json();
        const open = Array.isArray(data) ? data.map(t => ({ uid: t.uid, title: t.title, name: `${t.first_name} ${t.last_name}`, unread: t.unread_count || 0, last_at: t.updated_at, is_open: t.is_open })) : [];
        setTickets(open.filter(t => t.is_open));
      } else {
        const res = await fetch(`${API_BASE}/admin/tickets/`, { headers });
        if (!res.ok) { setTickets([]); setLoadingAdmin(false); return; }
        const data = await res.json();
        const list = Array.isArray(data) ? data : [];
        const filtered = which === 'completed' ? list.filter(t => t.is_open === false) : list;
        const mapped = filtered.map(t => ({ uid: t.uid, title: t.title, name: `${t.first_name} ${t.last_name}`, unread: t.unread_count || 0, last_at: t.updated_at, is_open: t.is_open }));
        setTickets(mapped);
      }
    } catch (e) {
      console.log('Failed load admin tickets', e);
      setTickets([]);
    } finally {
      setLoadingAdmin(false);
    }
  };

  // reload admin tickets when filter changes
  useEffect(() => {
    if (isAdmin) loadAdminTickets(adminFilter);
  }, [adminFilter, isAdmin]);

  const loadOpenCount = async () => {
    const token = await AsyncStorage.getItem('authToken');
    try {
      const res = await fetch(`${API_BASE}/tickets/my/?is_open=true`, { headers: token ? { Authorization: `Token ${token}` } : {} });
      if (res.ok) {
        const data = await res.json();
        const count = data.count != null ? data.count : (Array.isArray(data) ? data.length : 0);
        setOpenCount(count);
      }
    } catch (e) { /* ignore */ }
  };

  useEffect(() => {
    const id = setInterval(loadOpenCount, 5000);
    loadOpenCount();
    return () => clearInterval(id);
  }, []);

  useFocusEffect(
    useCallback(() => {
      loadOpenCount();
    }, [])
  );

  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [attachments, setAttachments] = useState([]);
  const [loading, setLoading] = useState(false);

  const pickFile = async () => {
    const res = await DocumentPicker.getDocumentAsync({ copyToCacheDirectory: true });
    if (res.type === 'success') {
      // ensure we have a size field; some platforms provide `size` directly
      let size = res.size;
      try {
        if (size == null && res.uri) {
          const r = await fetch(res.uri);
          const b = await r.blob();
          size = b.size;
        }
      } catch (e) {
        // ignore size fetch errors
      }
      setAttachments((s) => [...s, { ...res, size }]);
    }
  };

  const fmtSize = (bytes) => {
    if (!bytes && bytes !== 0) return '';
    const b = Number(bytes);
    if (isNaN(b)) return '';
    if (b < 1024) return `${b} B`;
    if (b < 1024 * 1024) return `${(b / 1024).toFixed(1)} KB`;
    return `${(b / (1024 * 1024)).toFixed(2)} MB`;
  };

  const removeAttachment = (index) => {
    setAttachments((s) => s.filter((_, i) => i !== index));
  };

  const submit = async () => {
    if (!firstName || !lastName || !title || !description) {
      Alert.alert('All fields are required');
      return;
    }
    if (!attachments.length) {
      Alert.alert('At least one attachment is required');
      return;
    }
    setLoading(true);
    try {
      const form = new FormData();
      form.append('first_name', firstName);
      form.append('last_name', lastName);
      form.append('title', title);
      form.append('description', description);
      // append each selected file under the same key 'attachments'
      for (const a of attachments) {
        const fileUri = a.uri;
        const fileName = a.name || 'attachment';
        // Convert file URI to blob for reliable upload across platforms
        const resp = await fetch(fileUri);
        const blob = await resp.blob();
        form.append('attachments', blob, fileName);
      }

      const token = await AsyncStorage.getItem('authToken');
      const res = await fetch(`${API_BASE}/tickets/create/`, {
        method: 'POST',
        headers: token ? { Authorization: `Token ${token}` } : {},
        body: form,
      });
      const data = await res.json();
      if (!res.ok) {
        Alert.alert('Error', JSON.stringify(data));
      } else {
  Alert.alert('Ticket created', `Your ticket id: ${data.uid}`);
  navigation.navigate('Chat', { ticketUid: data.uid });
  try { loadOpenCount(); } catch (e) { /* ignore */ }
      }
    } catch (err) {
      Alert.alert('Network error', String(err));
    } finally {
      setLoading(false);
    }
  };

  // Admin messenger-style view
  if (isAdmin) {
    return (
      <View style={global.container}>
        <View style={{ flexDirection: 'row', justifyContent: 'space-between', marginBottom: 12 }}>
          <TouchableOpacity onPress={async () => { await AsyncStorage.removeItem('authToken'); await AsyncStorage.removeItem('isAdmin'); navigation.replace('Login'); }}>
            <Text style={{ color: colors.primary, fontWeight: '800' }}>Logout</Text>
          </TouchableOpacity>
        </View>

        <View style={global.elevatedCard}>
          <View style={{ flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center' }}>
            <PillToggle options={[{ value: 'not_complete', label: 'Not Complete' }, { value: 'all', label: 'All' }, { value: 'completed', label: 'Completed' }]} value={adminFilter} onChange={setAdminFilter} />
            {loadingAdmin ? <Loading text="Loading..." /> : null}
          </View>
          <View style={{ height: 10 }} />
          <Text style={global.header}>Admin Notifications</Text>
          <Text style={[global.subText, { marginTop: 6 }]}>Quick access to recent user reports and conversations</Text>
        </View>

        {tickets.length === 0 ? (
          <Text style={global.subText}>No pending tickets</Text>
        ) : (
          tickets.map(t => (
            <View key={t.uid} style={global.card}>
              <TouchableTicket ticket={t} navigation={navigation} />
            </View>
          ))
        )}
      </View>
    );
  }

  // For regular users we keep a clean page; header has Open reports button

  return (
    <View style={global.container}>
      <View style={{ flexDirection: 'row', alignItems: 'center', justifyContent: 'space-between', marginBottom: 12 }}>
        <TouchableOpacity onPress={async () => { await AsyncStorage.removeItem('authToken'); await AsyncStorage.removeItem('isAdmin'); navigation.replace('Login'); }} style={{ padding: 6 }}>
          <Text style={{ color: colors.primary, fontWeight: '700' }}>Logout</Text>
        </TouchableOpacity>
        <TouchableOpacity onPress={() => navigation.navigate('MyReports')} style={{ flexDirection: 'row', alignItems: 'center' }}>
          <Text style={{ fontWeight: '800', marginRight: 8 }}>Open reports</Text>
          <View style={{ backgroundColor: colors.danger, borderRadius: 14, paddingHorizontal: 10, paddingVertical: 6 }}>
            <Text style={{ color: 'white', fontWeight: '800' }}>{openCount}</Text>
          </View>
        </TouchableOpacity>
      </View>

      <View style={global.elevatedCard}>
        <View style={{ flexDirection: 'row', alignItems: 'center' }}>
          <View style={global.avatar} />
          <View style={{ flex: 1 }}>
            <Text style={global.subHeader}>New Report</Text>
            <Text style={global.subText}>Weâ€™ll forward this to the support team. Attach any file that helps us assist you faster.</Text>
          </View>
        </View>

        <View style={global.separator} />

        <Text style={{ fontWeight: '700', marginBottom: 6 }}>Your details</Text>
        <TextInput value={firstName} onChangeText={setFirstName} style={global.input} placeholder="First name" />
        <TextInput value={lastName} onChangeText={setLastName} style={global.input} placeholder="Last name" />

        <Text style={{ fontWeight: '700', marginBottom: 6 }}>Issue</Text>
        <TextInput value={title} onChangeText={setTitle} style={global.input} placeholder="Short title" />
        <TextInput value={description} onChangeText={setDescription} style={[global.input, { height: 110 }]} multiline placeholder="Describe the issue in detail" />

        <TouchableOpacity onPress={pickFile} style={[global.secondaryButton, { alignSelf: 'flex-start', marginTop: 8 }]}>
          <Text style={global.buttonTextMuted}>Pick attachment</Text>
        </TouchableOpacity>

        {attachments.map((a, idx) => (
          <View key={idx} style={{ flexDirection: 'row', alignItems: 'center', marginTop: 10 }}>
            <Text style={{ flex: 1 }}>{a.name} {a.size ? `(${fmtSize(a.size)})` : ''}</Text>
            <TouchableOpacity onPress={() => removeAttachment(idx)} style={{ paddingHorizontal: 8, paddingVertical: 6 }}>
              <Text style={{ color: colors.danger }}>Remove</Text>
            </TouchableOpacity>
          </View>
        ))}

        <View style={{ height: 12 }} />
        <TouchableOpacity onPress={submit} disabled={loading} style={[global.button, { opacity: loading ? 0.6 : 1 }]}>
          {loading ? <Loading text="Sending..." /> : <Text style={global.buttonText}>Submit</Text>}
        </TouchableOpacity>
      </View>
    </View>
  );
}

function TouchableTicket({ ticket, navigation }) {
  return (
    <TouchableOpacity onPress={() => navigation.navigate('Chat', { ticketUid: ticket.uid })} style={{ padding: 12 }}>
      <View style={{ flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center' }}>
        <View style={{ flex: 1 }}>
          <Text style={{ fontWeight: '800', fontSize: 15 }}>{ticket.title}</Text>
          <Text style={global.subText}>{ticket.name}</Text>
        </View>
        <View style={{ alignItems: 'flex-end' }}>
          {ticket.unread ? <Text style={{ color: colors.danger, fontWeight: '800' }}>{ticket.unread} new</Text> : null}
          <Text style={{ color: global.subText?.color || '#999', fontSize: 12 }}>{ticket.last_at}</Text>
        </View>
      </View>
    </TouchableOpacity>
  );
}
