import React, { useEffect, useState } from 'react';
import { View, Text, FlatList, Button, TouchableOpacity } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { Platform } from 'react-native';

const API_BASE = Platform.OS === 'web' ? `http://${window.location.hostname}:8000/api` : 'http://10.0.2.2:8000/api';

export default function AdminPanel({ navigation }) {
  const [tickets, setTickets] = useState([]);

  useEffect(() => {
    (async () => {
      const token = await AsyncStorage.getItem('authToken');
      const res = await fetch(`${API_BASE}/admin/tickets/`, { headers: token ? { Authorization: `Token ${token}` } : {} });
      if (res.ok) {
        const data = await res.json();
        setTickets(data);
      }
    })();
  }, []);

  return (
    <View style={{ flex: 1, padding: 12 }}>
      <Text style={{ fontWeight: 'bold', fontSize: 18 }}>Admin - Tickets</Text>
      <FlatList data={tickets} keyExtractor={(t) => String(t.id)} renderItem={({ item }) => (
        <TouchableOpacity onPress={() => navigation.navigate('Chat', { ticketUid: item.uid })} style={{ padding: 8, borderBottomWidth: 1 }}>
          <Text style={{ fontWeight: 'bold' }}>{item.title}</Text>
          <Text>{item.first_name} {item.last_name}</Text>
          <Text>{item.created_at}</Text>
        </TouchableOpacity>
      )} />
    </View>
  );
}
