import React from 'react';
import { StyleSheet, TouchableOpacity, Text, View } from 'react-native';

export const colors = {
  primary: '#007bff',
  muted: '#eef',
  textMuted: '#666',
  success: 'green',
  danger: 'red'
};

export const global = StyleSheet.create({
  container: { flex: 1, padding: 12 },
  smallText: { fontSize: 12, color: colors.textMuted },
  pillContainer: { flexDirection: 'row', backgroundColor: colors.muted, borderRadius: 20, padding: 4 },
  pill: { paddingVertical: 6, paddingHorizontal: 12, borderRadius: 16, marginRight: 6 },
  pillText: { fontWeight: '600' }
});

export function PillToggle({ options = [], value, onChange }) {
  return (
    <View style={global.pillContainer}>
      {options.map(opt => (
        <TouchableOpacity
          key={opt.value}
          onPress={() => onChange(opt.value)}
          style={[global.pill, { backgroundColor: value === opt.value ? colors.primary : 'transparent' }]}
        >
          <Text style={[global.pillText, { color: value === opt.value ? '#fff' : colors.primary }]}>{opt.label}</Text>
        </TouchableOpacity>
      ))}
    </View>
  );
}

export default { colors, global, PillToggle };
